example.code:
import processing.candy.*;

SVG m;

void setup(){
  size(400,400);
  // The file "moo.svg" but be in the data folder
  // of the current sketch to load successfully
  m = new SVG(this, "moo.svg");
} 

void draw() {
  background(204);
  m.ignoreStyles();
  m.draw();
  m.ignoreStyles(false);
  fill(0, 90, 120);
  m.draw(0, 100);
}
