example.code:
import processing.candy.*;
import processing.xml.*;

SVG m;

void setup(){
  size(400,400);
  // The file "moo.svg" but be in the data folder
  // of the current sketch to load successfully
  m = new SVG("moo.svg",this);
} 

void draw() {
  background(204);
  m.ignoreStyles(false);
  m.draw();
  translate(0, 100);
  m.ignoreStyles(true);
  fill(0, 90, 120);
  m.draw();
}
