example.code:
PImage img = loadImage("rockies.jpg");
PImage img2 = loadImage("degaul.jpg"); 
img.blend(img2, 0, 0, 33, 100, 67, 0, 33, 100, ADD); 
image(img, 0, 0);
image(img2, 0, 0);

PImage img = loadImage("rockies.jpg");
PImage img2 = loadImage("degaul.jpg"); 
img.blend(img2, 0, 0, 33, 100, 67, 0, 33, 100, SUBTRACT); 
image(img, 0, 0);
image(img2, 0, 0);

PImage img = loadImage("rockies.jpg");
PImage img2 = loadImage("degaul.jpg"); 
img.blend(img2, 0, 0, 33, 100, 67, 0, 33, 100, DARKEST); 
image(img, 0, 0);
image(img2, 0, 0);

PImage img = loadImage("rockies.jpg");
PImage img2 = loadImage("degaul.jpg"); 
img.blend(img2, 0, 0, 33, 100, 67, 0, 33, 100, LIGHTEST); 
image(img, 0, 0);
image(img2, 0, 0);

root.syntax:
img.blend(sx, sy, dx, dy, mode);
img.blend(srcImg, sx, sy, dx, dy, mode);
img.blend(sx1, sy1, swidth, sheight, dx1, dy1, dwidth, dheight, mode);
img.blend(srcImg, sx1, sy1, swidth, sheight, dx1, dy1, dwidth, dheight, mode);
