PImage img1, img2;

void setup() {
  img1 = loadImage("archcrop.jpg");
  img2 = loadImage("archcrop.jpg");
  img1.filter(GRAY);
}

void draw() {
  image(img1, 0, 0);
  image(img2, width/2, 0);
}

PImage img1, img2;

void setup() {
  img1 = loadImage("archcrop.jpg");
  img2 = loadImage("archcrop.jpg");
  img1.filter(THRESHOLD, .6);
  img2.filter(THRESHOLD, .4);
}

void draw() {
  image(img1, 0, 0);
  image(img2, width/2, 0);
}
