example.code:
// The file "bot.svg" must be in the data folder
// of the current sketch to load successfully
PShape s;
s = loadShape("bot.svg");
smooth();
shape(s, 10, 10, 80, 80);      // Right shape
s.setVisible(false);
if (s.isVisible() == false) {  // of if (!s.isVisible) {
  noFill();
  rect(5, 5, 90, 90); 
}
