example.code:
PShape square;  // The PShape object

void setup() {  
  size(100, 100, P2D);
  // Creating the PShape as a square. The corner 
  // is 0,0 so that the center is at 40,40 
  square = createShape(RECT, 0, 0, 80, 80);
}

void draw() {
  shape(square, 10, 10);
}

PShape s;  // The PShape object

void setup() {  
  size(100, 100, P2D);
  // Creating the PShape as a square. The corner 
  // is 0,0 so that the center is at 40,40 
  s = createShape();
  s.fill(0, 0, 255);
  s.noStroke();
  s.vertex(0, 0);
  s.vertex(0, 50);
  s.vertex(50, 0);
  s.end();
}

void draw() {
  shape(s, 25, 25);
}

PShape s;

void setup() {
  size(100, 100, P2D);
  s = createShape(TRIANGLE_STRIP);
  s.vertex(30, 75);
  s.vertex(40, 20);
  s.vertex(50, 75);
  s.vertex(60, 20);
  s.vertex(70, 75);
  s.vertex(80, 20);
  s.vertex(90, 75);
  s.end();
}

void draw() {
  shape(s, 0, 0); 
}

// Define the individual shapes and the group
PShape alien, head, body;

void setup() {
  size(100, 100, P2D);
  alien = createShape(GROUP);
  // Make two shapes  
  head = createShape(ELLIPSE, 0, 0, 50, 50);
  head.noStroke();
  body = createShape(RECT, 0, 45, 50, 40);
  body.fill(0);
  // Add the two "child" shapes to the parent group
  alien.addChild(body);
  alien.addChild(head);
}

void draw() {
  translate(25, 10);
  shape(alien);  // Draw the group
}
