example.code:
void setup() {
  size(320, 240);
  background(153);
}

void draw() {
  line(0, 0, width, height);
}

void setup() {
  size(320, 240, P3D);
  background(153);
}

void draw() {
  line(0, 0, 0, width, height, -200);
}

import processing.opengl.*;

// Unless running in "Present" mode, this will not hide 
// the menu bar when using Mac OS X.
void setup() {
  size(screen.width, screen.height, OPENGL);
  background(153);
}

void draw() {
  line(0, 0, 0, width, height, -200);
}
