example.code:
PFont font;
// The font must be located in the sketch's 
// "data" directory to load successfully
font = loadFont("FFScala-32.vlw"); 
textFont(font); 
text("word", 15, 30); 
fill(0, 102, 153);
text("word", 15, 60);
fill(0, 102, 153, 51);
text("word", 15, 90);

size(100, 100, P3D);
PFont font;
font = loadFont("FFScala-32.vlw"); 
textFont(font); 
text("word", 15, 60, -30); 
fill(0, 102, 153);
text("word", 15, 60);

PFont font;
font = loadFont("FFScala-Bold-12.vlw"); 
textFont(font); 
String s = "The quick brown fox jumped over the lazy dog.";
text(s, 15, 20, 70, 70);
