example.code:
// Text to display. The "\n" is a "new line" character
String lines = "L1\nL2\nL3";
textSize(12);
fill(0);  // Set fill to black

textLeading(10);  // Set leading to 10
text(lines, 10, 25);

textLeading(20);  // Set leading to 20
text(lines, 40, 25);

textLeading(30);  // Set leading to 30
text(lines, 70, 25);
