example.code:
import processing.candy.*;
import processing.xml.*;

SVG m;

void setup(){
  size(400,400);
  // The file "moo.svg" must be in the data folder
  // of the current sketch to load successfully
  m = new SVG(this, "moo.svg");
} 

void draw(){
  m.drawMode(CORNER);
  m.draw(100, 100, 200, 200);
  m.drawMode(CORNERS);
  m.draw(100, 100, 200, 200);
  m.drawMode(CENTER);
  m.draw(100, 100, 200, 200);
}
