PImage b;

void setup() {
  b = loadImage("laDefense.jpg");
  noLoop();
}

void draw() {
  image(b, 0, 0);
}

PImage online;

void setup() {
  String url = "http://mt0.google.com/mt?n=404&v=w2.61&x=9913&y=12119&zoom=2";
  online = loadImage(url, "png");
  noLoop();
}

void draw() {
  image(b, 0, 0);
}
