example.code:
float a = PI;
float c = cos(a);
float ac = acos(c);
// Prints "3.1415927 : -1.0 : 3.1415927"
println(a + " : " + c + " : " +  ac);

float a = PI + PI/4.0;
float c = cos(a);
float ac = acos(c);
// Prints "3.926991 : -0.70710665 : 2.3561943"
println(a + " : " + c + " : " +  ac);

angleMode(DEGREES);
float a = 45;
float c = cos(a);
float ac = acos(c);
// Prints "45.0 : 0.70710677 : 45.0"
println(a + " : " + c + " : " +  ac);
