example.code:
angleMode(DEGREES);
float deg = 45;
float da = cos(deg);
angleMode(RADIANS);
float rad = PI/4; 
float ra = cos(rad);
// Prints "Cosine of PI/4 radians: 0.70710677"
println("Cosine of PI/4 radians: " + ra); 
// Prints "Cosine of 45 degress: 0.70710677"
println("Cosine of 45 degrees: " + da);
