example.code:
float a = PI/3;
float t = tan(a);
float at = atan(t);
// Prints "1.0471976 : 1.7320509 : 1.0471976"
println(a + " : " + t + " : " +  at);

float a = PI + PI/3.0;
float t = tan(a);
float at = atan(t);
// Prints "4.1887903 : 1.7320513 : 1.0471977"
println(a + " : " + t + " : " +  at);

angleMode(DEGREES);
float a = 30;
float t = tan(a);
float at = atan(t);
// Prints "30.0 : 0.57735026 : 30.0"
println(a + " : " + t + " : " +  at);
