example.code:
// Click on the window to give it focus
// and press the 'B' key

void loop() {
  if(keyPressed) {
    if (key == 'b' || key == 'B') {
      fill(0);
    }
  } else {
    fill(255);
  }
  rect(25, 25, 50, 50);
}

// Click on the window to give it focus
// and press the up and down arrow keys

void loop() {
  if(keyPressed) {
    if (key == UP) {
      fill(255);
    } else if (key == DOWN) {
      fill(0);
    }
  } else {
    fill(126);
  }
  rect(25, 25, 50, 50);
}
