example.code:
int val = 30;

void draw() {
  int t = timestwo(val);
  println(t);
}

int timestwo(int doubledVal) {
  doubledVal = doubledVal * 2;
  return doubledVal;
}

int[] vals = {10, 20, 30};

void draw() {
  int[] t = timestwo(vals);
  println(t);
}

int[] timestwo(int[] doubledVals) {
  for(int i=0; i<b.length; i++) {
    b[i] = b[i] * 2;
  }
  return doubledVals;
}
