example.code:
void loop() {
  float x = mouseX;
  float y = mouseY;
  float z = -100;

  // temporary kludge to kick P5 into 3D drawing
  translate(0, 0, 0);

  stroke(0);
  float theY = screenY(x, y, z);
  // Draw 2D line to match 3D position
  line(0, theY, width, theY);    

  stroke(255);
  // Draw line in 3D
  line(x-10, y, z, x+10, y, z);  

  // Draw line in 2D as comparison
  line(0, y, width, y);    
}
