example.code:
PFont metaBold;
// The font "Meta-Bold.vlw.gz" must be located in the 
// current sketch's "data" directory to load successfully 
metaBold = loadFont("Meta-Bold.vlw.gz");
// Text to display. The "\n" is a "new line" character
String lines = "LIN1\nLIN2\nLIN3"; 
textFont(metaBold);
textSize(16); 
textLeading(0);
text(lines, 8, 25); 
fill(126);  // Set value to gray
textLeading(16);
text(lines, 36, 25);
fill(0);    // Set value to black
textLeading(24);
text(lines, 64, 25);
