example.code:
// Declare and contruct two objects (h1, h2) from the class HLine
HLine h1 = new HLine(20, 1.0);
HLine h2 = new HLine(50, 5.0); 

void setup() { 
  size(200, 200); 
} 

void loop() {
  if(h2.speed > 1.0) {
    h2.speed -= 0.01;
  } 
  h1.update(); 
  h2.update();  
} 

class HLine { 
  float ypos, speed; 
  HLine (float y, float s) {  
    ypos = y; 
    speed = s; 
  } 
  void update() { 
    ypos += speed; 
    if (ypos > width) { 
      ypos = 0; 
    } 
    line(0, ypos, width, ypos); 
  } 
}
