example.code:
noStroke();
colorMode(RGB, 100);
for(int i=0; i<100; i++) {
  for(int j=0; j<100; j++) {
    stroke(i, j, 0);
    point(i, j);
  }
}

noStroke();
colorMode(HSB, 100);
for(int i=0; i<100; i++) {
  for(int j=0; j<100; j++) {
    stroke(i, j, 100);
    point(i, j);
  }
}

root.syntax:
colorMode(durum);
colorMode(durum, aralýk);
colorMode(durum, aralýk1, aralýk2, aralýk3);
