root.syntax:
blend(sx, sy, dx, dy, mode);
blend(srcImg, sx, sy, dx, dy, mode);
blend(sx1, sy1, sx2, sy2, dx1, dy1, dx2, dy2, mode);
blend(srcImg, sx1, sy1, sx2, sy2, dx1, dy1, dx2, dy2, mode);
