example.code:
int x = 0;
void loop()
{
  background(204);
  if(x < 100) {
    line(x, 0, x, 100);
    // Saves each frame as screen-0000.tif, screen-0001, etc.
    saveFrame(); 
    x = x + 1;
  }
}

int x = 0;
void loop()
{
  background(204);
  if(x < 100) {
    line(x, 0, x, 100);
    // Saves each frame as line-0000.tif, line-0001, etc.
    saveFrame("line-####.tif"); 
    x = x + 1;
  }
}
