example.code:
void loop() {
  float x = mouseX;
  float y = mouseY;
  float z = -100;

  // temporary kludge to kick P5 into 3D drawing
  translate(0, 0, 0);

  stroke(0);
  float theX = screenX(x, y, z);
  // Draw 2D line to match 3D position
  line(theX, 0, theX, height);    

  stroke(255);
  // Draw line in 3D
  line(x, y-10, z, x, y+10, z);  

  // Draw line in 2D as comparison
  line(x, 0, x, height);
}
