example.code:
BFont metaBold;
// The font "Meta-Bold.vlw.gz" must be located in the 
// current sketch's "data" directory to load successfully 
metaBold = loadFont("Meta-Bold.vlw.gz"); 
textFont(metaBold, 24);
textMode(ALIGN_RIGHT);
text("word", 50, 30); 
textMode(ALIGN_CENTER);
text("word", 50, 50); 
textMode(ALIGN_LEFT);
text("word", 50, 70);
