example.code:
int a=200, b=40, c=90;
String sa = nf(a, 10);
println(sa); // prints "0000000200"
String sb = nf(b, 5);
println(sb); // prints "00040"
String sc = nf(c, 3);
println(sc); // prints "090"

float d = 200.94, e = 40.2, f = 9.012;
String sd = nf(d, 10, 4);
println(sd);  // prints "0000000200.9400"
String se = nf(e, 5, 3);
println(se);  // prints "00040.200"
String sf = nf(f, 3, 5);
println(sf);  // prints "009.01200"
